import React from 'react';
import { useGlobalDispatch } from '../../utils/GlobalState';
import { idbPromise } from '../../utils/helpers';
import { ListGroup, Button, Row, Col, Form, Image } from 'react-bootstrap';

const CartItem = ({ item }) => {
  const dispatch = useGlobalDispatch();

  const removeFromCart = () => {
    dispatch({
      type: 'REMOVE_FROM_CART',
      payload: item._id || item.id
    });
    idbPromise('cart', 'delete', { ...item });
  };

  const onChange = (e) => {
    const value = e.target.value;
    
    if (value === '0') {
      dispatch({
        type: 'REMOVE_FROM_CART',
        payload: item._id || item.id
      });
      idbPromise('cart', 'delete', { ...item });
    } else {
      dispatch({
        type: 'UPDATE_CART_QUANTITY',
        payload: {
          _id: item._id || item.id,
          purchaseQuantity: parseInt(value),
          quantity: parseInt(value)
        }
      });
      idbPromise('cart', 'put', { 
        ...item, 
        purchaseQuantity: parseInt(value),
        quantity: parseInt(value)
      });
    }
  };

  return (
    <ListGroup.Item>
      <Row className="align-items-center">
        <Col xs={3} md={2}>
          <Image 
            src={item.images ? item.images[0] : "/marymirror.jpg"} 
            alt={item.name}
            thumbnail
          />
        </Col>
        <Col xs={9} md={4}>
          <h5>{item.name}</h5>
          <p className="text-muted">${item.price.toFixed(2)}</p>
        </Col>
        <Col xs={6} md={3}>
          <Form.Control
            type="number"
            placeholder="Qty"
            value={item.purchaseQuantity || item.quantity}
            onChange={onChange}
            min="0"
          />
        </Col>
        <Col xs={6} md={3} className="text-end">
          <Button 
            variant="danger" 
            size="sm"
            onClick={removeFromCart}
          >
            Remove
          </Button>
        </Col>
      </Row>
    </ListGroup.Item>
  );
};

export default CartItem;
