import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useStoreContext } from '../utils/GlobalState';
import { ADD_TO_CART } from '../utils/actions';
import { idbPromise } from '../utils/helpers';

function ProductCard({ product }) {
  const [state, dispatch] = useStoreContext();
  
  const addToCart = () => {
    // Check if this item is already in the cart
    const existingItem = state.cart.find(
      (cartItem) => cartItem._id === product._id || cartItem._id === product.id
    );
    
    // Create a cart-compatible product object
    const cartProduct = {
      ...product,
      _id: product._id || product.id, // Ensure _id exists for cart operations
      purchaseQuantity: 1
    };
    
    if (existingItem) {
      // If it's already in cart, just show a message
      alert(`${product.name} is already in your cart!`);
    } else {
      dispatch({
        type: ADD_TO_CART,
        payload: cartProduct
      });
      
      // Also save to IndexedDB
      idbPromise('cart', 'put', cartProduct);
      
      // Optional: Show feedback to user
      alert(`Added ${product.name} to cart!`);
    }
  };
  
  return (
    <Card className="vintage-card mb-4 h-100">
      <div className="product-image-container">
        <Card.Img 
          variant="top" 
          src={product.images ? product.images[0] : "/marymirror.jpg"} 
          alt={product.name}
          className="product-image"
        />
        <div className="era-badge">{product.era}</div>
      </div>
      <Card.Body className="d-flex flex-column">
        <Card.Title>{product.name}</Card.Title>
        <Card.Text className="text-muted mb-1">
          {product.category}
        </Card.Text>
        <Card.Text className="mb-3">
          ${product.price.toFixed(2)}
        </Card.Text>
        <div className="mt-auto d-flex justify-content-between">
          <Button 
            variant="outline-secondary" 
            as={Link} 
            to={`/product/${product._id || product.id}`}
          >
            Details
          </Button>
          <Button 
            variant="primary" 
            onClick={addToCart}
          >
            Add to Cart
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
}

export default ProductCard;
