import React from 'react';
import { useStoreContext } from '../../utils/GlobalState';
import { REMOVE_FROM_CART, UPDATE_CART_QUANTITY } from '../../utils/actions';
import { idbPromise } from '../../utils/helpers';
import { Row, Col, Button, Form } from 'react-bootstrap';

const CartItem = ({ item }) => {
  const [, dispatch] = useStoreContext();

  const removeFromCart = () => {
    dispatch({
      type: REMOVE_FROM_CART,
      payload: item._id
    });
    idbPromise('cart', 'delete', { ...item });
  };

  const onChange = (e) => {
    const value = parseInt(e.target.value);
    
    if (value === 0) {
      dispatch({
        type: REMOVE_FROM_CART,
        payload: item._id
      });
      idbPromise('cart', 'delete', { ...item });
    } else {
      dispatch({
        type: UPDATE_CART_QUANTITY,
        payload: {
          _id: item._id,
          purchaseQuantity: value
        }
      });
      idbPromise('cart', 'put', { ...item, purchaseQuantity: value });
    }
  };

  return (
    <div className="cart-item mb-3 p-2 border-bottom">
      <Row className="align-items-center">
        <Col xs={4} md={3}>
          <img 
            src={item.images ? item.images[0] : "/marymirror.jpg"} 
            alt={item.name}
            style={{ width: '100%', height: 'auto' }}
          />
        </Col>
        <Col xs={8} md={5}>
          <h6 className="mb-0">{item.name}</h6>
          <p className="text-muted mb-0">${item.price.toFixed(2)}</p>
        </Col>
        <Col xs={6} md={2} className="mt-2 mt-md-0">
          <Form.Control
            type="number"
            min="0"
            value={item.purchaseQuantity}
            onChange={onChange}
            size="sm"
          />
        </Col>
        <Col xs={6} md={2} className="text-end mt-2 mt-md-0">
          <Button 
            variant="outline-danger" 
            size="sm"
            onClick={removeFromCart}
          >
            Remove
          </Button>
        </Col>
      </Row>
    </div>
  );
};

export default CartItem;
